/* jshint expr: true */
module.exports = {
  tags: ['Core Front End'],
  before: function (browser) {
	  browser.windowMaximize('current');
  },
  'SANITY__Search_Refine_Search' : function (sanity) {
    sanity
    .url('http://corepreview:previewphase@core-cms.si.aop.cambridge.org/core/')
	//.url('http://core-cms.pen.aop.cambridge.org/core/')
      .pause(1000);
    sanity.waitForElementVisible('body',20000);
	sanity.element('xpath', "//a[@class='cookie-close']", function(result){
	if (result.status !=-1)
			sanity.useXpath().click("*//a[@class='cookie-close']").pause(5000);
	}), 
	sanity.element('xpath', "//a[@class='browser-message-close']", function(result){		
	if (result.status !=-1)
			sanity.useXpath().click("*//a[@class='browser-message-close']").pause(5000);	
	}), 	
	sanity.useXpath().setValue("*//input[@aria-label='Search all journal & book content']", 'Elegy for an Age').pause(500);
	sanity.useXpath().click("*//input[@value='Submit search']").pause(5000);
	sanity.useXpath().expect.element("*//h1[contains(.,'Search Results')]").to.be.visible;
	sanity.useXpath().click("*//*[@id='actionsContainer']//span[contains(.,'Only show open access')]/preceding-sibling::span[not(contains(@class,'icon'))]").pause(5000);
	sanity.useXpath().click("*//*[@id='actionsContainer']//span[contains(.,'Life Sciences')]/preceding-sibling::span[not(contains(@class,'icon'))]").pause(5000);
	sanity.useXpath().expect.element("*//span[contains(.,'Access: Only show open access')]").to.be.visible;
	sanity.useXpath().expect.element("*//span[contains(.,'Subject: Life Sciences')]").to.be.visible;
	sanity.end();
  },
  after:function(browser) {
	  browser.end();
  }
};
