/* jshint expr: true */
module.exports = {
  tags: ['Core Front End'],
  before: function (browser) {
	  browser.windowMaximize('current');
  },
  'SANITY__Search_Within' : function (sanity) {
    sanity
    .url('http://corepreview:previewphase@core-cms.si.aop.cambridge.org/core/')
	//.url('http://core-cms.pen.aop.cambridge.org/core/')
      .pause(1000);
   sanity.waitForElementVisible('body',20000);
	sanity.element('xpath', "//a[@class='cookie-close']", function(result){
	if (result.status !=-1)
			sanity.useXpath().click("*//a[@class='cookie-close']").pause(5000);
	}), 
	sanity.element('xpath', "//a[@class='browser-message-close']", function(result){		
	if (result.status !=-1)
			sanity.useXpath().click("*//a[@class='browser-message-close']").pause(5000);	
	}), 	
   	sanity.useXpath().click("*//a[contains(.,'What we publish')]").pause(5000);
	sanity.useXpath().click("*//ul/li[3]/a[contains(.,'Journals')]").pause(5000);
    sanity.useCss().setValue('#searchAndFilter', 'Journal of Agricultural and Applied Economics').pause(500);
    sanity.useXpath().click("*//a[contains(.,'Journal of Agricultural and Applied Economics')]").pause(5000);
	sanity.useCss().setValue('#journalSearch', 'Viability of Organic Production in Rural Counties: County and State-Level Evidence from the United States').pause(500);
	sanity.useXpath().click("*//input[contains(@class,'button postfix-radius')]").pause(5000);
	sanity.useXpath().expect.element(".//*[@id='maincontent']//span[2]/span[contains(.,'Viability of Organic')]").to.be.visible;
	sanity.useXpath().expect.element(".//*[@id='actionsContainer']//label[contains(.,'Journal of Agricultural and Applied Economics')]/span[@class='text-center text-primary']").to.be.visible;
	sanity.end();
  },
  after:function(browser) {
	  browser.end();
  }
};
