/* jshint expr: true */
module.exports = {
  tags: ['Core Front End'],
  before: function (browser) {
	  browser.windowMaximize('current');
  },
  'SANITY__Login_via_Athens' : function (sanity) {
    sanity
    .url('http://corepreview:previewphase@core-cms.si.aop.cambridge.org/core/')
	//.url('http://core-cms.pen.aop.cambridge.org/core/')
      .pause(1000);
    sanity.waitForElementVisible('body',20000);
	sanity.element('xpath', "//a[@class='cookie-close']", function(result){
	if (result.status !=-1)
			sanity.useXpath().click("*//a[@class='cookie-close']").pause(5000);
	}), 
	sanity.element('xpath', "//a[@class='browser-message-close']", function(result){		
	if (result.status !=-1)
			sanity.useXpath().click("*//a[@class='browser-message-close']").pause(5000);	
	}), 	
   	sanity.useXpath().click("*//li[5]/a[contains(.,'Log in')]").pause(5000);
	sanity.useXpath().click("*//div[3]//div/a[contains(.,'Log in')]").pause(5000);
	sanity.useXpath().click("*//a[contains(.,'Athens Login page')]").pause(5000);
	sanity.useXpath().setValue("*//input[@name='username']", 'cjoathenstest').pause(500);
	sanity.useXpath().setValue("*//input[@name='password']", 'CJOTest1ng!').pause(500);
	sanity.useXpath().click("*//button[contains(.,'Sign in')]").pause(5000);
	sanity.acceptAlert();
	sanity.useXpath().expect.element("*//div[@class='alert'][contains(.,'You are successfully logged in via your institutional account.')]").to.be.visible;
	sanity.end();
  },
  after:function(browser) {
	  browser.end();
  }
};
