/* jshint expr: true */
module.exports = {
  tags: ['Core Front End'],
  before: function (browser) {
	  browser.windowMaximize('current');
  },
  'SANITY__View_Core_reader_Chapter' : function (sanity) {
    sanity
    .url('http://corepreview:previewphase@core-cms.si.aop.cambridge.org/core/')
	//  .url('http://core-cms.pen.aop.cambridge.org/core/')
      .pause(1000);
	var data = "Exile as a profession, professions in exile";
    sanity.waitForElementVisible('body',20000);
	sanity.element('xpath', "//a[@class='cookie-close']", function(result){
	if (result.status !=-1)
			sanity.useXpath().click("*//a[@class='cookie-close']").pause(5000);
	}), 
	sanity.element('xpath', "//a[@class='browser-message-close']", function(result){		
	if (result.status !=-1)
			sanity.useXpath().click("*//a[@class='browser-message-close']").pause(5000);	
	}), 	
	
   	sanity.useXpath().click("*//li[5]/a[contains(.,'Log in')]").pause(5000);
	sanity.useCss().setValue('#username', 'corerachelall@cambridge.org').pause(500);
	sanity.useCss().setValue('#password', 'Password1').pause(500);
	sanity.useXpath().click("*//button[contains(.,'Log in')]").pause(5000);
	sanity.useXpath().setValue("*//input[@aria-label='Search all journal & book content']", data).pause(500);
	sanity.useXpath().click("*//input[@value='Submit search']").pause(5000);
	sanity.useXpath().expect.element(".//*[@id='maincontent']//span[2]/span[contains(.,'Exile as a profession, professions in exile')]").to.be.visible;
	sanity.useXpath().click("//a[contains(.,'3 - Exile as a profession, professions in exile')]").pause(5000);
	sanity.useXpath().click("*//a[contains(.,'View HTML')]").pause(5000);	
	sanity.end();
  },
  after:function(browser) {
	  browser.end();
  }
};