/* jshint expr: true */
module.exports = {
  tags: ['Core Front End'],
  before: function (browser) {
	  browser.windowMaximize('current');
  },
  'CORE-3659__Config_is_missing_on_QA_environment' : function (sanity) {
    sanity
    .url('http://corepreview:previewphase@core-cms.si.aop.cambridge.org/core/')
	//.url('http://core-cms.pen.aop.cambridge.org/core/')
      .pause(1000);
    sanity.waitForElementVisible('body',20000);
	sanity.element('xpath', "//a[@class='cookie-close']", function(result){
	if (result.status !=-1)
			sanity.useXpath().click("*//a[@class='cookie-close']").pause(5000);
	}), 
	sanity.element('xpath', "//a[@class='browser-message-close']", function(result){		
	if (result.status !=-1)
			sanity.useXpath().click("*//a[@class='browser-message-close']").pause(5000);	
	}), 	
   	sanity.useXpath().click("*//li[5]/a[contains(.,'Log in')]").pause(5000);
	sanity.useCss().setValue('#username', 'autocmsadmin@cambridge.org').pause(500);
	sanity.useCss().setValue('#password', 'Password1').pause(500);
	sanity.useXpath().click("*//button[contains(.,'Log in')]").pause(5000);
	sanity.useXpath().click("*//i[@class='fa fa-bars']").pause(5000);
	sanity.useXpath().click("*//a[@href='/core/dashboard/pages/sitemap']").pause(5000);
	sanity.useXpath().click("*//ul[2]/li[1]/a[contains(.,'System')]").pause(5000);
	sanity.useXpath().click("*//ul[2]/li[1]/a[contains(.,'System')]").pause(5000);
	sanity.useXpath().click("*//a[@href='/core/dashboard/system/config']").pause(5000);
	sanity.useCss().setValue('#dashboard-config-filter-input-input', 'core.registration.verification.instantLogin').pause(1000);
	sanity.useXpath().expect.element(".//*[@id='dashboard-config-entries']/div[41]/div[2]/div[1]/input[@value='false']").to.be.visible;
	sanity.useCss().clearValue('#dashboard-config-filter-input-input');
	sanity.useCss().setValue('#dashboard-config-filter-input-input', 'core.registration.verificationDisabled').pause(1000);
	sanity.useXpath().expect.element(".//*[@id='dashboard-config-entries']/div[42]/div[2]/div[1]/input[@value='false']").to.be.visible;
	sanity.useCss().clearValue('#dashboard-config-filter-input-input');
	sanity.useCss().setValue('#dashboard-config-filter-input-input', 'services.platform.email.sendRealEmails').pause(1000);
	sanity.useXpath().expect.element(".//*[@id='dashboard-config-entries']/div[83]/div[2]/div[1]/input[@value='false']").to.be.visible;
	sanity.end();
  },
  after:function(browser) {
	  browser.end();
  }
};
