/* jshint expr: true */
module.exports = {
  tags: ['Core Front End'],
  before: function (browser) {
	  browser.windowMaximize('current');
	  //browser.windowSize('current', 1920, 1080);
  },
  'SANITY__Login_as_CMS' : function (sanity) {
    sanity
     .url('http://corepreview:previewphase@core-cms.si.aop.cambridge.org/core/')
	 //.url('http://core-cms.pen.aop.cambridge.org/core/')
      .pause(1000);
    sanity.waitForElementVisible('body',20000);
	sanity.element('xpath', "//a[@class='cookie-close']", function(result){
	if (result.status !=-1)
			sanity.useXpath().click("*//a[@class='cookie-close']").pause(5000);
	}), 
	sanity.element('xpath', "//a[@class='browser-message-close']", function(result){		
	if (result.status !=-1)
			sanity.useXpath().click("*//a[@class='browser-message-close']").pause(5000);	
	}), 	
	
	sanity.useXpath().click("*//li[5]/a[contains(.,'Log in')]").pause(5000);
	sanity.useCss().setValue('#username', 'autocmsadmin@cambridge.org').pause(500);
	sanity.useCss().setValue('#password', 'Password1').pause(500);
	sanity.useXpath().click("*//button[contains(.,'Log in')]").pause(5000);
	
	//sanity.useXpath().waitForElementVisible("//a[contains(@id,'r-user-dropdown-icon')]", 5000, function () {
    //sanity.useXpath().moveToElement("//a[contains(@id,'r-user-dropdown-icon')]", 500, 500, function() {
	//	sanity.useXpath().waitForElementVisible("//a[contains(@id,'r-user-dropdown-icon')]", 5000, function () {
	//	sanity.useXpath().click("//a[contains(@id,'r-user-dropdown-icon')]");
	//	});
	//});
	//}),

    sanity.useXpath().moveToElement("//a[contains(@id,'r-user-dropdown-icon')]", 5000,5000);
	sanity.useXpath().click("//a[contains(@id,'r-user-dropdown-icon')]").pause(5000);
	sanity.useXpath().click("//a[contains(@id,'r-user-dropdown-icon')]").pause(5000);
	sanity.useXpath().waitForElementVisible("*//p[contains(.,'Logged in as CMS A')]",20000);
	sanity.useXpath().expect.element("*//i[contains(@class,'fa fa-edit text-')]").to.be.visible;
	sanity.useXpath().expect.element("*//i[@class='fa fa-bars']").to.be.visible;
	sanity.useXpath().expect.element("*//i[@class='fa fa-user']").to.be.visible;
	//sanity.useXpath().expect.element("*//a[contains(@href,'/dashboard') and contains(.,'Dashboard')]").to.be.visible;
	sanity.end();
  },
  after:function(browser) {
	  browser.end();
  }
};
